let WLSlider={urlStyle:"./styles/wl-slider.min.css",init:function(userOptions){try{this.style=this.setLink(this.urlStyle);this.options=this.setOptions(userOptions);this.container=document.getElementsByClassName("wl-slider")[0];this.contentArea=this.getContentArea("wl-slider_content flex");this.leftControlButton=this.getControlButton("wl-slider_control previous",this.options.get("controlButtons")["previous"]);this.rightControlButton=this.getControlButton("wl-slider_control next",this.options.get("controlButtons")["next"]);this.container.classList.add("flex");this.container.insertAdjacentElement("beforeend",this.leftControlButton);this.container.insertAdjacentElement("beforeend",this.contentArea);this.container.insertAdjacentElement("beforeend",this.rightControlButton);for(let i=0;i<this.options.get("content").length;i++){if(i==0)this.contentArea.insertAdjacentElement("beforeend",this.getSlide("#","wl-slider_slide visible",i,this.options.get("content")[i]));else this.contentArea.insertAdjacentElement("beforeend",this.getSlide("#","wl-slider_slide invisible",i,this.options.get("content")[i]))}this.leftControlButton.addEventListener("click",this.previousAction.bind(this));this.rightControlButton.addEventListener("click",this.nextAction.bind(this));this.interval=setTimeout(this.nextAction.bind(this),this.options.get("speed"))}catch(error){console.log("WLSlider error: "+error)}},setOptions:function(userOptions){let map=new Map;map.set("content",["./images/demo-content-1.jpg","./images/demo-content-2.jpg","./images/demo-content-3.jpg"]);map.set("speed",7e3);map.set("controlButtons",{previous:"./images/previous.png",next:"./images/next.png"});if(userOptions){for(key in userOptions){if(map.has(key)){map.set(key,userOptions[key])}}}return map},setLink:function(href){let links=document.getElementsByTagName("link");for(let i=0;i<links.length;i++){if(links[i].href.indexOf(href.slice(2))!=-1){return links[i]}}let style=document.createElement("link");style.rel="stylesheet";style.type="text/css";style.href=href;return document.head.appendChild(style)},getContentArea:function(className){let element=document.createElement("div");element.setAttribute("class",className);return element},getControlButton:function(className,imgUrl){let element=document.createElement("div");element.setAttribute("class",className);let img=document.createElement("img");img.setAttribute("src",imgUrl);element.insertAdjacentElement("beforeend",img);return element},getSlide:function(href,className,position,imgUrl){let element=document.createElement("a");element.setAttribute("href",href);element.setAttribute("class",className);element.setAttribute("style",'background: url("'+imgUrl+'");');element.setAttribute("position",position);return element},nextAction:function(){let slides=document.getElementsByClassName("wl-slider_slide");let currSlide=document.getElementsByClassName("wl-slider_slide visible")[0];let position=parseInt(currSlide.getAttribute("position"))+1;if(position>=slides.length)position=0;currSlide.classList.remove("visible");currSlide.classList.add("invisible");slides[position].classList.remove("invisible");slides[position].classList.add("visible");clearTimeout(this.interval);this.interval=setTimeout(this.nextAction.bind(this),this.options.get("speed"))},previousAction:function(){let slides=document.getElementsByClassName("wl-slider_slide");let currSlide=document.getElementsByClassName("wl-slider_slide visible")[0];let position=parseInt(currSlide.getAttribute("position"))-1;if(position<0)position=slides.length-1;currSlide.classList.remove("visible");currSlide.classList.add("invisible");slides[position].classList.remove("invisible");slides[position].classList.add("visible");clearTimeout(this.interval);this.interval=setTimeout(this.nextAction.bind(this),this.options.get("speed"))}};